import { useState } from "react";
import "./NewExpensesForm.css";
function NewExpensesForm() {
  const [enteredTitle, setEnteredTitle] = useState("");
  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
  };
  const [enteredAmount, setEnteredAmount] = useState('');
  const amountChangeHandler = (event)=>{
    setEnteredAmount(event.target.value);
  }
  const [enteredADate, setEnteredDate] = useState('');
  const dateChangeHandler = (event)=>{
    setEnteredDate(event.target.value);
  }
  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control" onChange={amountChangeHandler}>
          <label>Amount</label>
          <input type="number" />
        </div>
        <div className="new-expense__control" onChange={dateChangeHandler}>
          <label>Date</label>
          <input type="date" min="2019-01-01" max="2022-12-31" />
        </div>
      </div>
      <div className="new_expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
}
export default NewExpensesForm;
